scripts.setup:react:
yarn add react react-dom && yarn add -D @snowpack/plugin-react-refresh && if [ ! -f src/App.jsx ]; then printf 'import React from "react";

const App = () => <p>Hello, React.</p>;

export default App;

' > src/App.jsx; fi && mv src/index.js src/index.jsx && printf 'import React from "react";
import ReactDOM from "react-dom";
import App from "./App";

ReactDOM.render(<App />, document.getElementById("root"));

if (import.meta.hot) {
  import.meta.hot.accept();
}
'$'
'"$(cat src/index.jsx)" > src/index.jsx && sed -i '' 's/plugins: \[/plugins: [["@snowpack\/plugin-react-refresh"],/g' snowpack.config.js

scripts.eject:tailwind:
if [ -f tailwind.config.js ]; then yarn remove postcss tailwindcss autoprefixer && rm postcss.config.js tailwind.config.js; fi && printf '*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}' > src/index.css
