scripts.setup:react:
yarn add react react-dom && if [ ! -f src/App.jsx ]; then printf 'import React from "react";

const App = () => <p>Hello, React.</p>;

export default App;

' > src/App.jsx; fi && mv src/index.js src/index.jsx && printf 'import React from "react";
import ReactDOM from "react-dom";
import App from "./App";

ReactDOM.render(<App />, document.getElementById("root"));
'$'
'"$(cat src/index.jsx)" > src/index.jsx

scripts.eject:tailwind:
if [ -f tailwind.config.js ]; then yarn remove postcss postcss-cli tailwindcss autoprefixer && rm postcss.config.js tailwind.config.js; fi && printf '*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}' > src/index.css
