scripts.setup:react:
yarn add react react-dom && yarn add -D @babel/preset-react && if [ ! -f babel.config.json ]; then printf '{ "presets": ["@babel/preset-react"] }' > babel.config.json; fi && if [ ! -f src/App.js ]; then printf 'import React from "react";

const App = () => <p>Hello, React.</p>;

export default App;

' > src/App.js; fi && printf 'import React from "react";
import ReactDOM from "react-dom";
import App from "./App";

ReactDOM.render(<App />, document.getElementById("root"));
'$'
'"$(cat src/index.js)" > src/index.js

scripts.eject:tailwind:
if [ -f tailwind.source.css ]; then yarn remove postcss postcss-cli tailwindcss autoprefixer && rm postcss.config.js tailwind.config.js tailwind.source.css src/.tailwind.styles.css; fi && printf '*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}' > src/styles.css && sed -i '' 's/.tailwind.styles.css/styles.css/g' src/index.html
